/**
 * x402-stacks - Utility Functions
 * Helper functions for working with x402 payments on Stacks
 */
import { StacksMainnet, StacksTestnet } from '@stacks/network';
import { NetworkType, TokenType, TokenContract } from './types';
import { NetworkV2 } from './types-v2';
/**
 * Convert microSTX to STX
 */
export declare function microSTXtoSTX(microSTX: bigint | string): string;
/**
 * Convert STX to microSTX
 */
export declare function STXtoMicroSTX(stx: number | string): bigint;
/**
 * Convert sats to BTC
 */
export declare function satsToBTC(sats: bigint | string): string;
/**
 * Convert BTC to sats
 */
export declare function BTCtoSats(btc: number | string): bigint;
/**
 * Convert USDCx to micro-units (6 decimals, same as USDC)
 */
export declare function USDCxToMicroUSDCx(usdcx: number | string): bigint;
/**
 * Convert micro-USDCx to USDCx
 */
export declare function microUSDCxToUSDCx(microUSDCx: bigint | string): string;
/**
 * Generate a random Stacks keypair
 */
export declare function generateKeypair(network?: NetworkType): {
    privateKey: string;
    publicKey: string;
    address: string;
};
/**
 * Validate Stacks address format
 */
export declare function isValidStacksAddress(address: string): boolean;
/**
 * Check if address is mainnet or testnet
 */
export declare function getAddressNetwork(address: string): NetworkType | null;
/**
 * Get API endpoint for network
 */
export declare function getAPIEndpoint(network: NetworkType): string;
/**
 * Get block explorer URL for transaction
 */
export declare function getExplorerURL(txId: string, network?: NetworkType): string;
/**
 * Format payment amount for display
 */
export declare function formatPaymentAmount(amount: bigint | string, options?: {
    includeSymbol?: boolean;
    decimals?: number;
    tokenType?: TokenType;
}): string;
/**
 * Parse memo field from x402 payment
 */
export declare function parsePaymentMemo(memo: string): {
    resource?: string;
    nonce?: string;
    custom?: Record<string, string>;
};
/**
 * Create x402 memo string
 */
export declare function createPaymentMemo(resource: string, nonce: string, custom?: Record<string, string>): string;
/**
 * Calculate estimated fee for transaction
 */
export declare function estimateFee(transactionSize?: number, feeRate?: number): bigint;
/**
 * Wait with exponential backoff
 */
export declare function waitWithBackoff(attempt: number, baseDelayMs?: number, maxDelayMs?: number): Promise<void>;
/**
 * Retry function with exponential backoff
 */
export declare function retryWithBackoff<T>(fn: () => Promise<T>, maxAttempts?: number, baseDelayMs?: number): Promise<T>;
/**
 * Validate payment request expiration
 */
export declare function isPaymentRequestExpired(expiresAt: string): boolean;
/**
 * Create expiration timestamp
 */
export declare function createExpirationTimestamp(secondsFromNow: number): string;
/**
 * Truncate address for display
 */
export declare function truncateAddress(address: string, chars?: number): string;
/**
 * Get network instance from network type
 */
export declare function getNetworkInstance(network: NetworkType): StacksMainnet | StacksTestnet;
/**
 * Get default sBTC contract for network
 */
export declare function getDefaultSBTCContract(network: NetworkType): TokenContract;
/**
 * Get default USDCx contract for network
 * USDCx is Circle's USDC on Stacks via xReserve
 */
export declare function getDefaultUSDCxContract(network: NetworkType): TokenContract;
/**
 * Get token symbol for display
 */
export declare function getTokenSymbol(tokenType: TokenType): string;
/**
 * Get token decimals
 */
export declare function getTokenDecimals(tokenType: TokenType): number;
/**
 * Get smallest unit name for token
 */
export declare function getTokenSmallestUnit(tokenType: TokenType): string;
/**
 * Convert V1 network type to CAIP-2 format
 */
export declare function networkToCAIP2(network: NetworkType): NetworkV2;
/**
 * Convert CAIP-2 network identifier to V1 network type
 * @throws Error if the CAIP-2 identifier is not a valid Stacks network
 */
export declare function networkFromCAIP2(caip2: string): NetworkType;
/**
 * Check if a string is a valid Stacks CAIP-2 network identifier
 */
export declare function isValidStacksCAIP2(caip2: string): caip2 is NetworkV2;
/**
 * Convert V1 token type to V2 asset identifier
 * For STX, returns "STX"
 * For SIP-010 tokens, returns the contract identifier (address.name)
 */
export declare function assetToV2(tokenType: TokenType, tokenContract?: TokenContract): string;
/**
 * Convert V2 asset identifier to V1 token type
 * Returns the token type and optionally the contract info
 */
export declare function assetFromV2(asset: string): {
    tokenType: TokenType;
    tokenContract?: TokenContract;
};
